import React, { useEffect, useState } from "react";
import styles from "./Homepage.module.scss";
import Actions from "../components/actions/Actions";
import Clock from "../components/clock/Clock";
import Pomodoro from "../components/pomodoro/Pomodoro";
import Calendar from "../components/calendar/Calendar";
import Music from "../components/music/Music";
import Scenes from "../components/scenes/Scenes";
import { useSelector } from "react-redux";
import Player from "../components/player/Player";

function Homepage() {
  /*
    Local state
  */
  const { music, scene, pomodoro, buddy, calendar } = useSelector(
    (state) => state.ui
  );
  const { activeScene } = useSelector((state) => state.scenes);

  const playAudio = async () => {
    try {
      const audio = new Audio(`/assets/audio/moon.mp3`);
      await audio.play();
    } catch (error) {
      console.error("Error playing audio:", error);
    }
  };

  /*
    Effects
  */
  useEffect(function () {
    playAudio();
  }, []);

  /*
    JSX
  */
  return (
    <div className={styles.homepage}>
      <video
        key={activeScene}
        className={styles.homepage__video}
        autoPlay
        loop
        muted
      >
        <source src={`/assets/video/${activeScene}.mp4`} type="video/mp4" />
        Your browser doesn't support video.
      </video>

      <Player />

      <img
        src={`/assets/images/Logo.png`}
        alt="Logo"
        className={styles.homepage__logo}
      />

      <Actions />
      <Clock />
      {pomodoro && <Pomodoro />}
      {calendar && <Calendar />}
      {music && <Music />}
      {scene && <Scenes />}
    </div>
  );
}

export default Homepage;
