import React, { useReducer } from "react";
import styles from "./Calendar.module.scss";

const intialState = {
  day: new Date().getDate(),
  month: new Date().getMonth(),
  daysWorked: [],
};

function reducer(state, action) {
  switch (action.type) {
    case "calendar/created":
      return { ...state };

    case "calendar/dayWorked":
      // Adding day before completion
      if (state.daysWorked.includes(action.payload)) {
        alert("You already marked this day");
        return { ...state };
      }

      if (action.payload !== state.day) {
        alert(`Marking wrong day..`);
        return { ...state };
      }
      return { ...state, daysWorked: [...state.daysWorked, action.payload] };

    default:
      return { state };
  }
}

function Calendar() {
  const [state, dispatch] = useReducer(reducer, intialState);

  function handleDayworked(day) {
    dispatch({ type: "calendar/dayWorked", payload: day });
  }

  return (
    <div className={styles.calendar}>
      <div className={styles.calendar__container}>
        {Array.from({ length: 31 }, (_, index) => index + 1).map((el) => (
            <div className={styles.calendar__dayContainer}>

            </div>


        ))}
        <div className={styles.calendar__dayTasks}>
          <ul className={styles.calendar__list}>
            <li>Done-1</li>
            <li>Done-2</li>
            <li>Done-3</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Calendar;
