// Pomodoro.jsx
import React, { useEffect, useReducer } from "react";
import styles from "./Pomodoro.module.scss"; // Adjust the import based on your file structure

const initialState = {
  minutes: 25,
  seconds: 0,
  isWorking: true, // Indicates if it's a work session or break
};

function reducer(state, action) {
  switch (action.type) {
    case "timer/updated":
      return {
        ...state,
        seconds: state.seconds === 0 ? 59 : state.seconds - 1,
        minutes: state.seconds === 0 ? state.minutes - 1 : state.minutes,
        isWorking:
          state.minutes === 0 && state.seconds === 0
            ? !state.isWorking
            : state.isWorking,
      };
    default:
      return state;
  }
}

function Pomodoro() {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    const interval = setInterval(() => {
      dispatch({ type: "timer/updated" });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  const totalSeconds = state.minutes * 60 + state.seconds;
  const percentage = (totalSeconds / (25 * 60)) * 100;

  return (
    <div className={styles.pomodoro__container}>
      <div className={styles.pomodoro}>
        <div
          className={styles.progressBar}
          style={{ width: `${percentage}%` }}
        />
        <div className={styles.timer}>
          {String(state.minutes).padStart(2, "0")} :{" "}
          {String(state.seconds).padStart(2, "0")}
        </div>
        <div className={styles.sessionType}>
          {state.isWorking ? "Work Session" : "Break"}
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
